import React from "react";

function NavBar() {
    const navBar = document.querySelector(".nav-area");
    let lastScrollY = window.scrollY;

    window.addEventListener("scroll", () => {
        if(lastScrollY < window.scrollY) {
            navBar.classList.add("nav--hidden");
        } else {
            navBar.classList.remove("nav--hidden");
        }

        lastScrollY = window.scrollY;
    });

    return (
        <nav class="nav-area">
        <ul class="nav-links">
            <li><a class="nav-lst" href="#header">Home</a></li>
            <li><a class="nav-lst" href="#about-me">About</a></li>
            <li><a class="nav-lst" href="#technical-skills">Skills</a></li>
            {/* <li><a class="nav-lst" href="#project-section">Portfolio</a></li> */}
            <li><a class="nav-lst" href="#contact-me">Contact</a></li>
        </ul>
    </nav>
    )
}

export default NavBar;
