import React, { useState, useEffect } from "react";

window.onscroll = function (e) {
    var scrollY = window.pageYOffset || document.documentElement.scrollTop;
    var header = document.querySelector('nav-area');

    scrollY <= this.lastScroll
        ? header.style.visibility = 'visible'
        : header.style.visibility = 'hidden';

    this.lastScroll = scrollY;
}

function NavBar() {

    return (
        <nav class="nav-area">
            <ul class="nav-links">
                <li><a class="nav-lst" href="#header">Home</a></li>
                <li><a class="nav-lst" href="#about-me">About</a></li>
                <li><a class="nav-lst" href="#technical-skills">Skills</a></li>
                {/* <li><a class="nav-lst" href="#project-section">Portfolio</a></li> */}
                <li><a class="nav-lst" href="#contact-me">Contact</a></li>
            </ul>
        </nav>
    )
}

export default NavBar;
