import React, { useState, useEffect } from "react";

const navBar = () => {
    const [show, setShow] = useState(true);
    const controlNavBar = () => {
        if (window.scrollY > 100) {
            setShow(false);
        } else {
            setShow(true);
        }
    }
}

useEffect(() => {
    window.addEventListener('scroll',
        controlNavBar)
    return () => {
        window.removeEventListener('scroll', controlNavBar)
    }
}, [])

function NavBar() {

    return (
        <nav class="{nav-area}">
            <ul class="nav-links">
                <li><a class="nav-lst" href="#header">Home</a></li>
                <li><a class="nav-lst" href="#about-me">About</a></li>
                <li><a class="nav-lst" href="#technical-skills">Skills</a></li>
                {/* <li><a class="nav-lst" href="#project-section">Portfolio</a></li> */}
                <li><a class="nav-lst" href="#contact-me">Contact</a></li>
            </ul>
        </nav>
    )
}

export default NavBar;
