import React, { useState, useEffect } from "react";

function NavBar() {

    const [show, setShow] = useState(true);
    const [prevScrollPos, setPrevScrollPos] = useState(0);
    const [currntClassNme, setCurrntClassNme] = useState("nav-area-not-animated");

    const handleScroll = (() => {
        //find current scroll position
        const currentScrollPos = window.pageYOffset;

        if (prevScrollPos > currentScrollPos) {
            setCurrntClassNme("");
        }
        // set state based on location info (explained in more detail below)
        // setShow((prevScrollPos > currentScrollPos && prevScrollPos - currentScrollPos > 70) || currentScrollPos < 10);

        // setPrevScrollPos(currentScrollPos);
    }, 100);

    // new useEffect
    // useEffect(() => {
    //     window.addEventListener('scroll'.handleScroll);
    //     return () => window.removeEventListener('scroll', handleScroll);
    // }, [prevScrollPos, show, handleScroll]);

    return (
        <nav class={currntClassNme}>
            <ul class="nav-links">
                <li><a class="nav-lst" href="#header">Home</a></li>
                <li><a class="nav-lst" href="#about-me">About</a></li>
                <li><a class="nav-lst" href="#technical-skills">Skills</a></li>
                {/* <li><a class="nav-lst" href="#project-section">Portfolio</a></li> */}
                <li><a class="nav-lst" href="#contact-me">Contact</a></li>
            </ul>
        </nav>
    )
}

export default NavBar;
