import React, { useState } from "react";

function ProgressBar(percentage) {
    const [value, setValue] = useState();
    percentage = Math.round(percentage);
    // document.querySelector(".progress__fill").style.width = `${percentage}%`;
    // document.querySelector(".progress__text").textContent = `${percentage}%`;

    return (
        <div className="progress">
            <div className="progress__fill" style={{ width: value }}></div>
            <span className="progress__text">{value}%</span>
        </div >
    )
}

export default ProgressBar;
