import React from "react";

function updateProgBar(progressBar, value) {
    value = Math.round(value);
    progressBar.querySelector(".progress__fill").style.width = `${value}%`;
    progressBar.querySelector(".progress__text").textContent = `${value}%`;
}

function ProgressBar(percentage) {
    percentage = Math.round(percentage);
    progressBar.querySelector(".progress__fill").style.width = `${percentage}%`;
    progressBar.querySelector(".progress__text").textContent = `${percentage}%`;

    return (
        <div className="progress">
            <div className="progress__fill" style="width: {percentage}%;"></div>
            <span className="progress__text">{percentage}%</span>
        </div>
    )
}

export default ProgressBar;
